import { animate, style, transition, trigger } from '@angular/animations';
import { Component } from '@angular/core';
import { DownloadService } from '../services/download.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  animations: [
    trigger('fadeInOut', [
      transition(':enter', [
        style({ opacity: 0, transform: 'translateY(-20px)' }),
        animate('300ms ease-out', style({ opacity: 1, transform: 'translateY(0)' }))
      ])
    ])
  ],
  styleUrls: ['./home.component.scss']
})
export class HomeComponent {
  youtubeUrl!: string;
  darkMode = false;
  isDownloading: boolean = false;
  progress: number = 0;
  selectedFormat: string = 'mp4';
  selectedQuality: string = '1080p';

  constructor(private downloadService: DownloadService) {}

  onDownload() {
    if (this.youtubeUrl) {
      this.isDownloading = true;
      this.progress = 0;
      this.downloadService.downloadVideo(this.youtubeUrl).subscribe(response => {
        this.downloadFile(response);
        this.isDownloading = false;
      }, error => {
        console.error('Download failed', error);
        this.isDownloading = false;
      });
      const interval = setInterval(() => {
        if (this.progress >= 100) {
          clearInterval(interval);
        } else {
          this.progress += 10;
        }
      }, 500);
    }
  }

  toggleDarkMode() {
    this.darkMode = !this.darkMode;
  }

  private downloadFile(data: Blob) {
    const blob = new Blob([data], { type: 'video/mp4' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'video.mp4'; // You can specify the filename here
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    window.URL.revokeObjectURL(url); // Clean up the URL object
  }
}
